INSERT INTO `publicnews` (`news_id`, `news_title`, `news_detail`, `news_date`, `view`) VALUES
(5, 'ประกาศรับนักเรียนใหม่ ระดับชั้นอนุบาล 1 และมัธยมศึกษาชั้นปีที่ 4 ปีการศึกษา 2560', '<h3>ผู้ที่สนใจสามารถขอรับ และยื่นใบสมัครได้ที่ห้องศูนย์ฝึกประสบการณ์ฯ&nbsp;อาคาร 1 ชั้น 1&nbsp;คณะครุศาสตร์&nbsp;<br />\r\nตั้งแต่วันที่&nbsp;<strong>26 ธันวาคม 2559 - 24 กุมภาพันธ์ 2560 น.</strong>&nbsp;ภาคเช้าเวลา&nbsp;<strong>9.00 - 12.00</strong>&nbsp;น.&nbsp;<br />\r\nและภาคบ่าย เวลา&nbsp;<strong>13.00 - 16.30</strong>&nbsp;น. (หยุดวันเสาร์ - อาทิตย์ และวันนักขัตฤกษ์)&nbsp;<br />\r\nหมายเลขโทรศัพท์ และ โทรสาร (fax)&nbsp;054-241229&nbsp;<br />\r\n<br />\r\n1.ค่าใบสมัครระดับชั้นอนุบาลปีที่ 1&nbsp;ชุดละ 100 บาท และค่าสมัครเข้าเรียนคนละ 200 บาท รวม 300 บาท&nbsp;<br />\r\n2.ค่าใบสมัครระดับชั้นมัธยมศึกษาปีที่ 4 ชุดละ 200 บาท และค่าสมัครเข้าเรียนคนละ 200 บาท รวม 400 บาท</h3>\r\n\r\n<p><strong>หลักฐานการสมัครระดับชั้นอนุบาล (ให้นำฉบับจริงมาด้วยทุกฉบับ)</strong></p>\r\n\r\n<table border="1" cellpadding="1" cellspacing="1" style="width:500px">\r\n	<tbody>\r\n		<tr>\r\n			<td>1.สำเนาสูติบัตรนักเรียน</td>\r\n			<td>จำนวน 1 ฉบับ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>2.สำเนาทะเบียนบ้าน (ที่ระบุชื่อนักเรียน)</td>\r\n			<td>จำนวน 1 ฉบับ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>3.สำเนาทะเบียนบ้าน บิดา</td>\r\n			<td>จำนวน 1 ฉบับ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>4.สำเนาทะเบียนบ้าน มารดา</td>\r\n			<td>จำนวน 1 ฉบับ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>5.สำเนาบัตรประจำตัวประชาชน บิดา</td>\r\n			<td>จำนวน 1 ฉบับ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>6.สำเนาบัตรประจำตัวประชาชน มารดา</td>\r\n			<td>จำนวน 1 ฉบับ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>7.สำเนาสมุดบันทึกสุขภาพแม่และเด็กจากโรงพยาบาล</td>\r\n			<td>จำนวน 1 ฉบับ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>8.อื่นๆ (ถ้ามี)</td>\r\n			<td>จำนวน 1 ฉบับ</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><strong>หลักฐานการสมัครระดับมัธยมศึกษา ชั้นปีที่ 4 (ให้นำฉบับจริงมาด้วยทุกฉบับ)</strong></p>\r\n\r\n<table border="1" cellpadding="1" cellspacing="1" style="width:500px">\r\n	<tbody>\r\n		<tr>\r\n			<td>1.สำเนาบัตรประจำตัวประชาชน</td>\r\n			<td>จำนวน 1 ฉบับ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>2.สำเนาทะเบียนบ้าน (ที่ระบุชื่อนักเรียน)</td>\r\n			<td>จำนวน 1 ฉบับ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>3.สำเนาทะเบียนบ้าน บิดา</td>\r\n			<td>จำนวน 1 ฉบับ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>4.สำเนาทะเบียนบ้าน มารดา</td>\r\n			<td>จำนวน 1 ฉบับ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>5.สำเนาบัตรประจำตัวประชาชน บิดา</td>\r\n			<td>จำนวน 1 ฉบับ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>6.สำเนาบัตรประจำตัวประชาชน มารดา</td>\r\n			<td>จำนวน 1 ฉบับ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>7.สำเนาหลักฐานการสำเร็จการศึกษาระดับชั้นมัธยมศึกษาตอนต้น</td>\r\n			<td>จำนวน 1 ฉบับ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>8.อื่นๆ (ถ้ามี)</td>\r\n			<td>จำนวน 1 ฉบับ</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><strong>*กรุณาเตรียมหลักฐานการสมัครให้ครบถ้วนในวันยื่นใบสมัคร</strong></p>\r\n', '2017-10-20', 114),
(6, 'ประกาศโรงเรียนสาธิตมหาวิทยาลัยราชภัฏลำปาง เรื่อง การรับสมัครนักเรียน ระดับชั้นอนุบาล ๑ และมัธยมศึกษาปีที่ ๔', '<h1>ประกาศโรงเรียนสาธิตมหาวิทยาลัยราชภัฏลำปาง เรื่อง การรับสมัครนักเรียน ระดับชั้นอนุบาล ๑ และมัธยมศึกษาปีที่ ๔</h1>\r\n', '2017-10-20', 52),
(7, 'ประกาศโรงเรียนสาธิตมหาวิทยาลัยราชภัฏลำปาง เรื่อง การรับสมัครนักเรียน ระดับชั้นอนุบาล ๑ และอนุบาล ๒ (เพิ่มเติม)', '<h1>ประกาศโรงเรียนสาธิตมหาวิทยาลัยราชภัฏลำปาง เรื่อง การรับสมัครนักเรียน ระดับชั้นอนุบาล ๑ และอนุบาล ๒ (เพิ่มเติม)</h1>\r\n', '2017-10-30', 55),
(8, 'ประกาศผลการสอบสัมภาษณ์ผู้ปกครองและนักเรียน ระดับอนุบาล ๒ และระดับชั้นอนุบาล ๒ โรงเรียนสาธิตมหาวิทยาลัยราชภัฏลำปาง', '<h1>ประกาศผลการสอบสัมภาษณ์ผู้ปกครองและนักเรียน ระดับอนุบาล ๒ และระดับชั้นอนุบาล ๒ โรงเรียนสาธิตมหาวิทยาลัยราชภัฏลำปาง</h1>\r\n', '2017-10-30', 62),
(9, 'กำหนดการปฐมนิเทศผู้ปกครองนักเรียนระดับอนุบาลประกาศผลการสอบสัมภาษณ์ผู้ปกครองและนักเรียน ระดับอนุบาล ๒ และระดับชั้นอนุบาล ๒ โรงเรียนสาธิตมหาวิทยาลัยราชภัฏลำปาง (เพิ่มเติม)', '<h1 style="text-align:center">กำหนดการปฐมนิเทศผู้ปกครองนักเรียน</h1>\r\n\r\n<h1 style="text-align:center">ระดับอนุบาลประกาศผลการสอบสัมภาษณ์ผู้ปกครอง</h1>\r\n\r\n<h1 style="text-align:center">และนักเรียน ระดับอนุบาล ๒ และระดับชั้นอนุบาล ๒</h1>\r\n\r\n<h1 style="text-align:center">โรงเรียนสาธิตมหาวิทยาลัยราชภัฏลำปาง (เพิ่มเติม)</h1>\r\n', '2017-10-30', 39),
(10, 'ประกาศ ผลการคัดเลือก ผู้ทรงคุณวุฒิภายนอกของโรงเรียนสาธิตมหาวิทยาลัยราชภัฏลำปาง', '<h1 style="text-align:center"><span style="color:#1abc9c"><samp>ประกาศมหาวิทยาลัยราชภัฏลำปาง<br />\r\nเรื่อง&nbsp;ผลการคัดเลือกกรรมการอำนวยการผู้ทรงคุณวุฒิภายนอกของโรงเรียนสาธิตมหาวิทยาลัยราชภัฏลำปาง&nbsp;</samp></span></h1>\r\n', '2017-10-30', 111),
(11, 'ประกาศ อัตราและข้อกำหนดเกี่ยวกับการชำระค่าธรรมเนียม ค่าบำรุง และค่าใช้จ่ายอื่นๆ พ.ศ. ๒๕๕๙', '<p>ประกาศโรงเรียนสาธิตมหาวิทยาลัยราชภัฏลำปาง&nbsp;เรื่อง อัตราและข้อกำหนดเกี่ยวกับการชำระค่าธรรมเนียม ค่าบำรุง และค่าใช้จ่ายอื่นๆ ของโรงเรียนสาธิตมหาวิทยาลัยราชภัฏลำปาง พ.ศ. ๒๕๕๙</p>\r\n', '2017-11-03', 169),
(12, 'ประกาศรับสมัครนักเรียนโรงเรียนสาธิตมหาวิทยาลัยราชภัฏลำปาง ปีการศึกษา ๒๕๖๑', '<h3 style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Courier New,Courier,monospace"><span style="font-size:18px">&nbsp; &nbsp; &nbsp;โรงเรียนสาธิตมหาวิทยาลัยราชภัฏลำปาง กำหนดรับนักเรียน เข้าศึกษาในระดับก่อนประถมศึกษาแผนกเตรียมอนุบาล และแผนกอนุบาล ประจำปีการศึกษา ๒๕๖๑&nbsp;</span></span></h3>\r\n\r\n<h3 style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Courier New,Courier,monospace"><span style="font-size:18px"><strong>ระดับชั้นที่เปิดรับสมัคร</strong></span></span></h3>\r\n\r\n<h3 style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Courier New,Courier,monospace"><span style="font-size:18px"><strong>๑.๑ <u>แผนกเตรียมอนุบาล</u></strong></span></span></h3>\r\n\r\n<h3 style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Courier New,Courier,monospace"><span style="font-size:18px">&nbsp; &nbsp; รับสมัครนักเรียนที่มีอายุตั้งแต่ ๑ ปี ๓ เดือน ขึ้นไป โดยการส่งเสริมให้เด็กเตรียมอนุบาลได้รับการพัฒนาด้านร่างกาย&nbsp; อารมณ์&nbsp; สังคม&nbsp; และสติปัญญา&nbsp; เพื่อให้มีความพร้อมในการศึกษาต่อในระดับอนุบาลต่อไป</span></span></h3>\r\n\r\n<p>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style="font-family:Courier New,Courier,monospace"><span style="font-size:18px">รับสมัครทั้งปี<strong> </strong>เวลา&nbsp; ๐๘.๓๐-๑๖.๓๐&nbsp; น. เว้นวันหยุดราชการ&nbsp; </span></span></p>\r\n\r\n<h3 style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Courier New,Courier,monospace"><span style="font-size:18px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ณ โรงเรียนสาธิตมหาวิทยาลัยราชภัฏลำปาง</span></span></h3>\r\n\r\n<h3 style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Courier New,Courier,monospace"><span style="font-size:18px"><strong>๑.๒ <u>แผนกอนุบาล</u> </strong>รับสมัครนักเรียน</span></span></h3>\r\n\r\n<h3 style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Courier New,Courier,monospace"><span style="font-size:18px"><strong>&nbsp; &nbsp; ระดับชั้นอนุบาล ๑</strong>&nbsp;&nbsp;&nbsp; อายุระหว่าง ๓ &ndash; ๔ ขวบ &nbsp;อายุครบ ๓ &nbsp;ปีบริบูรณ์ นับถึงวันที่ ๑๖ พฤษภาคม ในปีการศึกษาที่เข้าเรียน</span></span></h3>\r\n\r\n<h3 style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Courier New,Courier,monospace"><span style="font-size:18px"><strong>&nbsp; &nbsp; &nbsp;ระดับชั้นอนุบาล ๒&nbsp;</strong>&nbsp;&nbsp; อายุระหว่าง ๔ &ndash; ๕ ขวบ</span></span></h3>\r\n\r\n<h3 style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Courier New,Courier,monospace"><span style="font-size:18px"><strong>&nbsp; &nbsp; &nbsp;ระดับชั้นอนุบาล ๓</strong>&nbsp;&nbsp;&nbsp; อายุระหว่าง ๕ &ndash; ๖ ขวบ</span></span></h3>\r\n\r\n<h3 style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Courier New,Courier,monospace"><span style="font-size:18px">โดยต้องผ่านการประเมิน ดังนี้</span></span></h3>\r\n\r\n<h3 style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Courier New,Courier,monospace"><span style="font-size:18px">&nbsp; &nbsp; &nbsp;๑.๒.๑ การประเมินผู้เรียน</span></span></h3>\r\n\r\n<h3 style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Courier New,Courier,monospace"><span style="font-size:18px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;จะทำการประเมินความพร้อมของผู้เรียนด้านร่างกาย อารมณ์ สังคม และสติปัญญา ตามระดับการเรียนรู้ (ไม่มีการประเมินการอ่าน-เขียน)</span></span></h3>\r\n\r\n<h3 style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Courier New,Courier,monospace"><span style="font-size:18px">&nbsp; &nbsp; &nbsp;๑.๒.๒ การสัมภาษณ์ผู้ปกครอง </span></span></h3>\r\n\r\n<h3 style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Courier New,Courier,monospace"><span style="font-size:18px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;จะทำการสัมภาษณ์ผู้ปกครอง ด้านการอบรมเลี้ยงดูนักเรียน และด้านประวัติสุขภาพของนักเรียน</span></span></h3>\r\n\r\n<h3 style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span style="font-family:Courier New,Courier,monospace"><span style="font-size:18px">กำหนดการรับสมัคร</span></span></h3>\r\n\r\n<h3 style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Courier New,Courier,monospace"><span style="font-size:18px">&nbsp;&nbsp;&nbsp;&nbsp;<strong><u>รอบที่ ๑ รับสมัคร</u></strong>&nbsp; วันที่&nbsp;&nbsp; ๖&nbsp; พฤศจิกายน&nbsp; ถึง&nbsp;&nbsp; ๒๙&nbsp;&nbsp; ธันวาคม&nbsp;&nbsp; ๒๕๖๐ เวลา ๐๘.๓๐-๑๖.๓๐ น. เว้นวันหยุดราชการ </span></span><span style="font-family:Courier New,Courier,monospace"><span style="font-size:18px">ณ โรงเรียนสาธิตมหาวิทยาลัยราชภัฏลำปาง</span></span></h3>\r\n\r\n<h3 style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Courier New,Courier,monospace"><span style="font-size:18px">&nbsp;&nbsp;&nbsp;</span></span><span style="font-family:Courier New,Courier,monospace"><span style="font-size:18px"><strong><u>รอบที่ ๒ รับสมัคร</u></strong>&nbsp; วันที่&nbsp; ๑&nbsp; กุมภาพันธ์&nbsp; ถึง&nbsp; ๖ &nbsp;เมษายน ๒๕๖๑ &nbsp; เวลา ๐๘.๓๐-๑๖.๓๐ น. เว้นวันหยุดราชการ </span></span><span style="font-family:Courier New,Courier,monospace"><span style="font-size:18px">ณ โรงเรียนสาธิตมหาวิทยาลัยราชภัฏลำปาง</span></span></h3>\r\n\r\n<h3 style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Courier New,Courier,monospace"><span style="font-size:18px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style="font-family:Courier New,Courier,monospace"><span style="font-size:18px"> </span></span></h3>\r\n', '2017-11-03', 257),
(13, 'ประกาศรายชื่อนักเรียนที่มีสิทธิเข้ารับการสัมภาษณ์และประเมินพัฒนาการ ประจำปีการศึกษา ๒๕๖๑ (รอบที่ ๑)', '<p><span style="color:#2980b9"><strong>ประกาศรายชื่อนักเรียนที่มีสิทธิเข้ารับการสัมภาษณ์และประเมินพัฒนาการ ประจำปีการศึกษา ๒๕๖๑ (รอบที่ ๑)</strong></span></p>\r\n\r\n<p><span style="color:#2980b9"><strong>ให้นักเรียนและผู้ปกครองที่มีรายชื่อ (เอกสารดังแนบ) เข้ารับการสัมภาษณ์และประเมินพัฒนาการ</strong></span></p>\r\n\r\n<p><span style="color:#2980b9"><strong>ในวันพุธที่ ๑๐ มกราคม ๒๕๖๑ เวลา ๐๙.๓๐ น. เป็นต้นไป ณ แผนกอนุบาล โรงเรียนสาธิตมหาวิทยาลัยราชภัฏลำปาง</strong></span></p>\r\n', '2018-01-03', 7);
